class Solution {
    // Method to print a triangle pattern
    void printTriangle(int n) {
        // Loop to iterate through each row of the triangle
        for(int i=1;i<=n;i++){
	        // Loop to print spaces before the asterisks
	        for(int j=1;j<i;j++){
	           System.out.print(" ");
	        }
	        // Loop to print asterisks in each row
	        for(int j=1;j<=2*(n-i)+1;j++){
	            System.out.print("*");
	        }
	        // Print a new line after each row
	        System.out.println();
	    }
    }
}

