class Solution:
    #Function to print a triangle pattern using asterisks.
    def printTriangle(self, N):
        #loop for each row of the triangle.
        for row in range(N, 0, -1):
            #loop to print spaces before each row.
            for space in range(N - row):
                print(' ', end = '')

            #loop to print asterisks for each row.
            for asterisk in range(1, 2 * row):
                print('*', end = '')
            print() #printing a new line after each row.
