class Solution {
    // function to return sum of elements
    // in an array of size n
  public:
    // function to print a triangle pattern
    void printTriangle(int n) {
        int c = 1; // variable to store the current number
        for (int i = 1; i <= n; i++) { // loop for each row
            for (int j = 1; j <= i; j++) { // loop for each number in the row
                cout << c << " "; // printing the current number
                c++; // incrementing the current number
            }
            cout << endl; // next line after each row
        }
    }
};

