class Solution {
    // function to return sum of elements
    // in an array of size n
  public:
    void printTriangle(int n) {
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j < i; j++) {
                cout << (char)(65 + j);
            }
            cout << endl;
        }
    }
};

class Solution {
    //Method to print a triangle pattern
    void printTriangle(int n) {
        //Outer loop to control the number of rows in the triangle
        for (int i = 1; i <= n; i++) {
            //Inner loop to print characters in each row
            for (int j = 0; j < i; j++) {
                //Printing characters using ASCII values
                System.out.print((char)(65 + j));
            }
            //Moving to next line
            System.out.println();
        }
    }
}

#Back-end complete function Template for Python 3

class Solution:
    #Function to print a triangle.
    def printTriangle(self, N):
        for i in range(N):
            for j in range(i + 1):
                #Printing the alphabets from A to the corresponding index.
                print(chr(65 + j), end = '')
            print()

//User function Template for javascript
/**
 * @param {number} N
 * @returns {void}
*/

class Solution {
    //Function to print a triangle.
    printTriangle(N) {
        for (let i = 0; i < N; i++) {
            for (let j = 0; j <= i; j++) {
                //Printing the alphabets from A to the corresponding index.
                process.stdout.write(String.fromCharCode(65 + j));
            }
            console.log();
        }
    }
}
