class Solution {
    // function to return sum of elements
    // in an array of size n
  public:
    // function to print a triangle pattern
    // with n number of rows
    void printTriangle(int n) {
        for (int i = n; i >= 1; i--) {
            // loop to print characters
            // in each row
            for (int j = 0; j < i; j++) {
                cout << (char)(65 + j);
            }
            cout << endl; // printing a new line after each row
        }
    }
};

class Solution {

    // method to print triangle pattern
    void printTriangle(int n) {
        // loop to iterate through each row of the triangle
        for (int i = n; i >= 1; i--) {
            for (int j = 0; j < i; j++) {
                // print characters from 'A' to 'A' + i - 1
                System.out.print((char)(65 + j));
            }
            // print new line after each row
            System.out.println();
        }
    }
}

#Back-end complete function Template for Python 3

class Solution:
    #Function to print the triangle pattern.
    def printTriangle(self, N):
        #iterating over the rows in reverse order.
        for i in range(N, 0, -1):
            #iterating over the columns in each row.
            for j in range(i):
                #printing the characters starting from 'A' based on the column index.
                print(chr(65 + j), end = '')
            #printing a new line after each row.
            print()

