class Solution {
    // function to return sum of elements
    // in an array of size n
  public:
    void printTriangle(int n) {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= i; j++) {
                cout << (char)(65 + i);
            }
            cout << endl;
        }
    }
};

java.full_func:
class Solution {
    
    // Function to print a triangle of characters
    void printTriangle(int n) {
        // Loop for each row of the triangle
        for (int i = 0; i < n; i++) {
            // Loop for each character in the row
            for (int j = 0; j <= i; j++) {
                // Print the character based on its ASCII value
                System.out.print((char)(65 + i));
            }
            System.out.println(); // Move to the next line after each row
        }
    }
}

