//User function Template for javascript
/**
* @param {number} N
*/

class Solution {
    //Function to print pattern.

    printTriangle(N)
    {
        for (let i = 65; i < 65 + N; i++) {
            for (let j = 65; j <= i; j++) {
                process.stdout.write(String.fromCharCode(i));
            }
            console.log();
        }
    }
}
