// Function to print a triangle pattern
class Solution {

  public:
    void printTriangle(int n) {

        // Loop through each row
        for (int i = 1; i <= n; i++) {

            // Print spaces before the first character
            for (int j = n - i; j >= 1; j--) {
                cout << " ";
            }

            // Print characters in increasing order
            for (int j = 1; j <= i; j++) {
                cout << (char)(j + 65 - 1);
            }

            // Print characters in decreasing order
            for (int j = i - 1; j >= 1; j--) {
                cout << (char)(j + 65 - 1);
            }

            // Move to next row
            cout << endl;
        }
    }
};

class Solution {

