class Solution {
    // function to return sum of elements
    // in an array of size n
  
  public:
  
    // function to print the triangle pattern
    void printTriangle(int n) {
        // iterate over rows
        for (int i = 1; i <= n; i++) {
            // iterate over columns
            for (int j = n - 1; j >= n - i; j--) {
                // print the current character
                cout << (char)(65 + j) << " ";
            }
            // move to the next line
            cout << endl;
        }
    }
};

java.full_func:
class Solution {

    // Method to print a triangle pattern
    void printTriangle(int n) {

        // Loop to print each row of the triangle
        for (int i = 1; i <= n; i++) {

            // Loop to print each element of the row
            // starting from the last character
            for (int j = n - 1; j >= n - i; j--) {

                // Print the character corresponding to the index
                // by adding the ASCII value of 'A' (65)
                System.out.print((char)(65 + j) + " ");
            }

            // Print a new line after printing each row
            System.out.println();
        }
    }
}

