#Back-end complete function Template for Python 3

class Solution:

    #Function to print a triangle pattern.
    def printTriangle(self, N):

        #Loop to iterate from N to 1.
        for i in range(N, 0, -1):

            #Loop to print the characters in each row.
            for j in range(N, i-1, -1):

                #Printing the characters using ASCII values.
                print(chr(j - 1 + 65), end = ' ')

            #Printing a new line after each row.
            print()

