//User function Template for javascript
/**
 * @param {number} N
 * @returns {void}
*/

class Solution {
    //Function to print pattern with N lines using numbers from 1 to N.
    printTriangle(N) {
        //Loop to iterate from N to 1.
        for (let i = N; i >= 1; i--) {

            //Loop to print the characters in each row.
            for (let j = N; j >= i; j--) {

                //Printing the characters using ASCII values.
                process.stdout.write(String.fromCharCode(j - 1 + 65) + ' ');
            }

            //Printing a new line after each row.
            console.log();
        }
    }
}
