// Back-end complete function Template for C++

class Solution{
public:
    // Function to check if a number is trimorphic or not.
    int isTrimorphic(int N){
        // Converting N to long long int.
        long long int n = N;
        // Calculating the cube of N.
        long long int cubed = n*n*n;

        // Loop to check each digit of N and cubed.
        while(n > 0){
            // If any digit does not match, return 0.
            if(n%10 != cubed%10)
                return 0;
            // Remove the last digit from N and cubed.
            n /= 10;
            cubed /= 10;
        }
        // If all digits matched, return 1.
        return 1;
    }
};

