#Back-end complete function Template for Python 3


def sequence(tup):

    ans = list(tup)

    d = tup[1] - tup[0]  # d = a1-a0

    for _ in range(3):

        ans.append(ans[-1] + d)

    return tuple(ans)

// User function Template for javascript
/**
 * @param {number[]} tup
 * @returns {Array}
 */

class Solution {
    // Function to return the tuple containing whole sequences.
    sequence(tup) {
        let ans = [...tup ];

        let d = tup[1] - tup[0]; // d = a1-a0

        for (let i = 0; i < 3; i++) {
            ans.push(ans[ans.length - 1] + d);
        }

        return ans;
    }
}
