class Solution
{
    public:
        //Function to count the number of words in the list that appear twice.
        int countWords(string list[], int n)
        {
            //Creating an unordered map to store the count of each word.
            unordered_map<string, int> m;

            //Iterating over the list to count the occurrences of each word.
            for (int i = 0; i < n; i++)
                m[list[i]] += 1;

            //Initializing a count variable to store the count of words that appear twice.
            int count = 0;

            //Iterating over the unordered map to find the words that appear twice.
            for (auto it = m.begin(); it != m.end(); it++)
                if ((it->second == 2))
                    count++;

            //Returning the count of words that appear twice.
            return count;
        }

};

