from collections import defaultdict

class Solution:

    #function to count the occurrence of each word
    def countWords(self,List, n):
        map = defaultdict(int)

        #counting the occurrence of each word using dictionary
        for e in List:
            map[e] += 1

        count = 0
        #counting the words which have occurred twice
        for k, v in map.items():
            if v == 2:
                count += 1
        return count

class Solution
{
    //Function to count the number of words in the list that appear twice.
    public int countWords(string[] list, int n)
    {
        //Creating a dictionary to store the count of each word.
        Dictionary<string, int> dict = new Dictionary<string, int>();

        //Iterating over the list to count the occurrences of each word.
        for (int i = 0; i < n; i++)
        {
            if (dict.ContainsKey(list[i]))
            {
                dict[list[i]]++;
            }
            else
            {
                dict[list[i]] = 1;
            }
        }

        //Initializing a count variable to store the count of words that appear twice.
        int count = 0;

        //Iterating over the dictionary to find the words that appear twice.
        foreach (var pair in dict)
        {
            if (pair.Value == 2)
            {
                count++;
            }
        }

        //Returning the count of words that appear twice.
        return count;
    }
}

