/**
 * @param {string[]} list
 * @param {number} n
 * @return {number}
 */
class Solution {
  countWords(list, n) {
    //code here
    let m = {};
    for (let i = 0; i < n; i++) {
        if (m[list[i]]) {
            m[list[i]] += 1;
        } else {
            m[list[i]] = 1;
        }
    }

    let count = 0;
    for (let key in m) {
        if (m[key] === 2) {
            count++;
        }
    }

    return count;
  }
}
