int maxNtype(int arr[], int n) {
    if (n == 1) {
        return 2;
    }

    int max = 0, index = 0, type;

    for (int i = 0; i < n; i++) {
        if (arr[i] > max) {
            max = arr[i];
            index = i;
        }
    }

    if (arr[0] == max && arr[n - 2] > arr[n - 1]) {
        type = 2;
    } else if (arr[n - 1] == max && arr[1] > arr[0]) {
        type = 1;
    } else if (arr[(n + index - 1) % n] > arr[(n + index + 1) % n]) {
        type = 4;
    } else {
        type = 3;
    }

    return type;
}

class Solution {
  public:
    int maxNtype(vector<int>& arr) {
        int n = arr.size();
        int max = 0, index = 0, type;
        for (int i = 0; i < n; i++) {
            if (arr[i] > max) {
                max = arr[i];
                index = i;
            }
        }
        if (arr[0] == max && arr[n - 2] > arr[n - 1])
            type = 2;

        else if (arr[n - 1] == max && arr[1] > arr[0])
            type = 1;

        else if (arr[(n + index - 1) % n] > arr[(n + index + 1) % n])
            type = 4;

        else
            type = 3;

        return type;
    }
};

