class Solution {
    maxNtype(arr) {
        // code here
        const n = arr.length;
        let max = 0, index = 0, type = 0;

        // Find the maximum element and its index.
        for (let i = 0; i < n; i++) {
            if (arr[i] > max) {
                max = arr[i];
                index = i;
            }
        }

        // Determine the type based on the given conditions.
        if (arr[0] === max && arr[n - 2] > arr[n - 1]) {
            type = 2;
        } else if (arr[n - 1] === max && arr[1] > arr[0]) {
            type = 1;
        } else if (arr[(n + index - 1) % n] > arr[(n + index + 1) % n]) {
            type = 4;
        } else {
            type = 3;
        }

        return type;
    }
}
