//Back-end complete function template in C++

class Solution {
	public:
		//Function to determine the input type of a string.
		string FindInputType(string str) {
			//Checking if the string has only one character.
			if (str.size() == 1) {
				//If the character is a digit, return "int", else return "char".
				if (str[0] >= '0' and str[0] <= '9')
					return "int";
				return "char";
			}

			//Initializing flags to determine the presence of different types of characters in the string.
			bool f1 = false; //Flag for digits.
			bool f2 = false; //Flag for non-digits.
			bool f3 = false; //Flag for '.'.
			int cnt = 0; //Variable to count the number of '.' occurrences.

			//Iterating over the characters in the string.
			for (auto i : str) {
				//Checking if the character is a digit.
				if (i >= '0' and i <= '9')
					f1 = true;
				//Checking if the character is '.'.
				else if (i == '.') {
					f3 = true;
					cnt++;
				}
				//If the character is neither a digit nor '.', then it is a non-digit character.
				else
					f2 = true;
			}

			//Checking the conditions to determine the input type based on the flags.
			//If there are only digits in the string and no other characters, return "int".
			if (f1 and !f2 and !f3)
				return "int";
			//If there are only digits and one '.' character in the string, and the '.' is not the last character, return "float".
			if (f1 and !f2 and f3 and cnt == 1 and str[str.size() - 1] != '.')
				return "float";
			//If none of the above conditions are met, return "string".
			return "string";
		}

};
