//Back-end complete function Template for C++

int upBound(vector<int>& arr, int k) {
    auto it = upper_bound(arr.begin(), arr.end(), k);
    if (it != arr.end())
        return *it;
    else
        return -1;
}

int lowBound(vector<int>& arr, int k) {
    auto it = lower_bound(arr.begin(), arr.end(), k);
    if (it != arr.end())
        return *it;
    else
        return -1;
}
