//Back-end complete function Template for C++
int multiply(int a, int b) {
    return a * b;
}

int multiplyUsingfunPointer(int a, int b) {
    int (*func)(int, int);

    // func is pointing to the multiplyTwoValues function

    func = multiply;

    int prod = func(a, b);

    return prod;
}
