/**
 * @param {number[]} arr
 * @returns {number[]}
 */
class Solution {
    // Function to find elements in the array that are equal to their index.
    valueEqualToIndex(arr) {
        let result = [];
        // iterating over all the elements in the array
        for (let i = 0; i < arr.length; i++) {
            // checking if the element is equal to its index + 1
            if (arr[i] === i + 1) {
                // adding the element to the result array
                result.push(arr[i]);
            }
        }
        // returning the result array with elements that are equal to their index
        return result;
    }
}
