// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the value of a polynomial at a given value of x
    int value(int n, int poly[], int x){
        long temp = 1, res = 0;
        long mod = 1000000007;

        // Iterating through all the coefficients of the polynomial
        for(int i = n-1;i >= 0;i--){
            // Multiplying the coefficient with the power of x and adding to the result
            res = (res + (temp * poly[i])%mod) % mod;

            // Updating the power of x
            temp = (temp * x)%mod;
        }

        // Returning the result modulo 1000000007
        return (res % mod);
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    public int value(int n, int[] poly, int x)
    {
        long temp = 1, res = 0;
        long mod = 1000000007;

        // Iterating through all the coefficients of the polynomial
        for (int i = n - 1; i >= 0; i--)
        {
            // Multiplying the coefficient with the power of x and adding to the result
            res = (res + (temp * poly[i]) % mod) % mod;

            // Updating the power of x
            temp = (temp * x) % mod;
        }

        // Returning the result modulo 1000000007
        return (int)(res % mod);
    }
}
