vector<int> fillVector(int arr[], int n) {
    vector<int> res;
    for (int i = 0; i < n; i++)
        res.push_back(arr[i]);
    return res;
}

class Solution
{
    //Complete this function
    //Function to insert the given array elements to vector.
    public List<int> fillVector(int[] arr, int n)
    {
        List<int> res = new List<int>();
        for(int i = 0; i < n; i++)
        {
            res.Add(arr[i]);
        }
        return res;
    }
}
