//Back-end complete function template for C++

class Solution{   
public:
    //Function to check if a character is a vowel or not.
    bool checkVowel(char c){
        //Return true if the character is 'a', 'e', 'i', 'o', or 'u'
        return(c=='a'||c=='A'||c=='e'||c=='E'||c=='i'||c=='I'||c=='o'||c=='O'||c=='u'||c=='U');
    }

    //Function to check if a character is a vowel or not and return "YES" or "NO" accordingly.
    string isVowel(char c){
        //Calling the checkVowel function and returning "YES" if it returns true, and "NO" otherwise.
        return checkVowel(c)?"YES":"NO";
    }
};

