class Solution {
    isVowel(c) {
        let v = "aeiouAEIOU".split(""); // Define an array containing all lowercase and uppercase vowels.
        for (let it of v) { // Iterate through each element of the array.
            if (it === c) // Check if the current element is equal to the input character.
                return "YES"; // Return "YES" if the input character is a vowel.
        }
        return "NO"; // Return "NO" if the input character is not a vowel.
    }
}
