#include <stdio.h>

int main() {
    int x;
    scanf("%d", &x);
    // use while loop over x
    // to print integers from x to 0
    // code here
    while (x >= 0) {
        printf("%d ", x);
        x -= 1;
    }

    return 0;
}

#include <iostream>
using namespace std;

int main() {
    int x;
    cin >> x;
    while (x >= 0) {
        cout << x << " ";
        x -= 1;
    }
    return 0;
}

