class Solution{
public:
    // Function to check if a number is Woodall number or not.
    int isWoodall(int N){
        // If the number is even, it cannot be a Woodall number.
        if(N%2 == 0)
            return 0;
        // If the number is 1, it is a Woodall number.
        if(N == 1)
            return 1;
        // Increment N by 1 and initialize power to 0.
        N++;
        int p = 0;
        // Divide N by 2 until it becomes odd and track the value of power.
        while(N%2 == 0){
            N = N/2;
            p++;
            // If power becomes equal to N, it is a Woodall number.
            if(p == N)
                return 1;
        }
        // If power does not become equal to N, it is not a Woodall number.
        return 0;
    }
};

