int countWrongPlacedBalls(string a)
{
    int c=0,i;
    // traversing whole string.
    for(i=0;i<a.length();i++)
    {
        // conditions are opposite as there is 1 based 
        // indexing as mentioned in question.
        if(i%2!=0)
        {
            if(a[i]=='R')
                c++;
        }
        else
        {
            if(a[i]=='B')
                c++;
        }
    }
    return c;
}

