class Solution
{
    //Complete this function
    //Function to count the number of wrong placed balls.
    public int countWrongPlacedBalls(string s)
    {
        int c=0, i;
        // traversing whole string.
        for(i=0;i<s.Length;i++)
        {
            // conditions are opposite as there is 1 based 
            // indexing as mentioned in question.
            if(i%2!=0)
            {
                if(s[i]=='R')
                    c++;
            }
            else
            {
                if(s[i]=='B')
                    c++;
            }
        }
        return c;
    }
}
