//Back-end complete function Template for Java

class Geeks{

    //function to print an array
    static void printArr(int arr[],int n)
    {
        //iterating over the array and printing each element
        for(int i=0;i<n;i++)
        System.out.print(arr[i]+" ");

        //printing a new line after printing the array
        System.out.println();
    }

   //function to set all elements of the array to zero
   static void setToZero(int arr[],int n)
    {
        //using the Arrays.fill() method to fill the array with zeros
        Arrays.fill(arr,0);
    }

   //function to perform XOR operation of array elements with their indices
   static void xor1ToN(int arr[], int n)
    {
        //iterating over the array
        for(int i=0;i<n;i++)
        {
            //performing XOR operation of each element with its index (i)
            arr[i]=arr[i]^i;
        }
    }
}
